digits_list = [int(d) for d in str()]
products_description = []
products_list = []

for i in range(len(digits_list) - 13):
    j = digits_list[i:i+13]
    products_description.append(j)

for k in products_description:
    a = [int(f) for f in k]
    products_list.append(a[0]*a[1]*a[2]*a[3]*a[4]*a[5]*a[6]*a[7]*a[8]*a[9]*a[10]*a[11]*a[12])

b = products_list.index(max(products_list))

print("The largest 13 digit product in the series of the number is ", max(products_list))
print("And the sequence is ", products_description[b])

        